<?php$section_type = 'banners';$section = 'Pages Banners';if (isset($_POST['submit1'])) {    $vals = $_POST;    unset($vals['submit1']);    if ($img_rs = uploadImage($_FILES["news_bg"], "../uploads/banners/",1920)) {        $vals['news_bg'] = $img_rs;    }    if ($img_rs = uploadImage($_FILES["general_bg"], "../uploads/banners/",1920)) {        $vals['general_bg'] = $img_rs;    }    if ($img_rs = uploadImage($_FILES["counter_bg"], "../uploads/banners/",1920)) {        $vals['counter_bg'] = $img_rs;    }    if ($img_rs = uploadImage($_FILES["contact_us"], "../uploads/banners/")) {        $vals['contact_us'] = $img_rs;    }    $exist = getField('tbl_texts', " `txt_type` = '$section_type'  ", 'txt_id');    if (!isset($exist) || empty($exist)) {        saveData('tbl_texts', array('txt_type' => $section_type));    }    $new_vals['txt_data'] = serialize($vals);    updateRecord("tbl_texts", $new_vals, " `txt_type` = '$section_type' ");    $_SESSION['successMsg'] = 'Changing has been updated successfully !';}$rs = getTable('tbl_texts', " `txt_type` = '$section_type' ");$data = unserialize(stripslashes($rs['txt_data']));?><?php include_once("pages/shared/sidebar.php"); ?><div class="content-wrapper">    <section class="content-header">        <h1><?= $section; ?></h1>        <?php echo getBredcrum(array('#' => $section)); ?>    </section>    <section class="content">        <div class="row">            <div class="col-md-12"><?= showMsg(); ?></div>            <div class="col-md-12">                <form action="" id="form1" method="post" class="form-horizontal" enctype="multipart/form-data">                    <div class="box box-info">                        <div class="box-body">                            <div class="col-sm-8 col-sm-offset-1">                                                               <?= formImageFile('General Background', 'general_bg', $data['general_bg'], '1920px x 325px', 'banners') ?>               